<script>

    $(function () {

        var rating = 1.6;

        $(".counter").text(rating);

        $("#rateYo1").on("rateyo.init", function () { console.log("rateyo.init"); });

        $("#rateYo1").rateYo({
            rating: rating,
            numStars: 5,
            precision: 2,
            starWidth: "32px",
            spacing: "5px",
            rtl: true,
            multiColor: {

                startColor: "#d7d6d4",
                endColor  : "#cacccb"
            },
            onInit: function () {

                console.log("On Init");
            },
            onSet: function () {

                console.log("On Set");
            }
        }).on("rateyo.set", function () { console.log("rateyo.set"); })
            .on("rateyo.change", function () { console.log("rateyo.change"); });

        $(".rateyo").rateYo();

        $(".rateyo-readonly-widg").rateYo({

            rating: rating,
            numStars: 5,
            precision: 2,
            minValue: 1,
            maxValue: 5
        }).on("rateyo.change", function (e, data) {

            console.log(data.rating);
        });
    });
</script>

